import React, { Component } from 'react';
import './App.css';


//import { create } from 'ipfs-http-client'
//const ipfs = create({ host: 'localhost', port: '5001' })

//const IPFS=require('ipfs-http-client');
//const ipfs2 =IPFS({host:'ipfs.infura.io',port:5001,protocol:'https'});

const ipfsClient = require('ipfs-http-client')
const ipfs = ipfsClient({ host: 'ipfs.infura.io', port: 5001, protocol: 'https' })

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      buffer: null,
      memeHash: 'QmYVR17hRbEyqzXHj1U7618B9m5TqdpVPqYcZp97bj165P'
    };
  }

  captureFile = (event) => {
    event.preventDefault()
    console.log('file captured!')
    // process file for IPFS
    const file = event.target.files[0]
    const reader = new window.FileReader()
    reader.readAsArrayBuffer(file)
    reader.onloadend = () => {
      this.setState({ buffer: Buffer(reader.result) })
      console.log('buffer', this.state.buffer)
    }
  }

  // Example Hash = QmXfojCPuMEv94vRBEFVKgRhn4pd6EBbECHFFgnMMD68oB
  // Example url = https://ipfs.infura.io/ipfs/QmXfojCPuMEv94vRBEFVKgRhn4pd6EBbECHFFgnMMD68oB
  onSubmit = (event) => {
    event.preventDefault()
    console.log("Submiting the form..")

    ipfs.add(this.state.buffer, (error,result) => {
      console.log('IPFS Result', result);
      const memeHash = result[0]
      this.setState({ memeHash: memeHash})
      if(error) {
        console.error("aaaaaaaaaaaaaaaa"+error)
        return
      }
      // 2. Store file in BC

    })
    // Burada iÅŸlem yap.
  }

  render() {
    return (
      <div>
        <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
          <a
            className="navbar-brand col-sm-3 col-md-2 mr-0"
            href="http://www.dappuniversity.com/bootcamp"
            target="_blank"
            rel="noopener noreferrer"
          >
            Hello Lacin
          </a>
        </nav>
        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 d-flex text-center">
              <div className="content mr-auto ml-auto">
                <a
                  href="http://www.dappuniversity.com/bootcamp"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <img src={`https://ipfs.infura.io/ipfs/${this.state.memeHash}`} />
                </a>
                <p>&nbsp;</p>
                <h2>Change meme</h2>
                <form onSubmit={this.onSubmit}>
                  <input type="file" onChange={this.captureFile} />
                  <input type="submit"/>
                </form>
              </div>
            </main>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
